function handler(event) {
    var request = event.request;
    var uri = request.uri;

    // Check if the URI is for a file (has a file extension)
    if (!uri.match(/\/[^\/]*\.[^\/]*$/)) {
        // Not a file, rewrite to /admin/index.html
        request.uri = '/admin/index.html';
    }
